import * as actionTypes from './actionTypes';

// named just as identifiers
export const addIngredient = (name) => {
    return{
        type: actionTypes.ADD_ING,
        ingredient: name
    }
}
export const removeIngredient = (name) => {
    return{
        type: actionTypes.REMOVE_ING,
        ingredient: name
    }
}
