import * as actionTypes from "./actions";

const initialState = {
  ingredients: null,
  totalPrice: 10,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
      case actionTypes.ADD_ING:
          return state;
      case actionTypes.REMOVE_ING:
          return state;
  }
  return state;
};

export default reducer;
